insert into `destoon_sell_data_5` (`itemid`, `content`) values('485','<p style=\"text-indent: 2em;\"><strong><span style=\"font-size: 14px;\">招生对象：</span></strong></p><p style=\"text-indent: 2em;\"><span style=\"font-size: 14px;\">准备出国留学，或者申请国外舞蹈类大学的学生。</span></p><p style=\"text-indent: 2em;\"><strong><span style=\"font-size: 14px;\">课程特点:</span></strong></p><p style=\"text-indent: 2em;\"><span style=\"font-size: 14px;\">国内短期集中训练 打好舞蹈基础 短期进修提高 辅助完成申请作品集 协助完成国外大学申请</span></p><p style=\"text-indent: 2em;\"><strong><span style=\"font-size: 14px;\">课程设置:</span></strong></p><p style=\"text-indent: 2em;\"><span style=\"font-size: 14px;\"><strong >体能训练：</strong>体能是每个舞者都需要备的素质，如果体力达不到，就无法达到完美的表演。</span></p><p style=\"text-indent: 2em;\"><span style=\"font-size: 14px;\"><strong >基本功强化：</strong>老师团队会针对不同舞蹈风格，分化不同舞蹈基本功，以芭蕾、现代、hiphop、Raggae、waackin、</span></p><p style=\"text-indent: 2em;\"><span style=\"font-size: 14px;\">funk、house、kumpin分别教授不同的基本功训练方法。</span></p><p style=\"text-indent: 2em;\"><span style=\"font-size: 14px;\"><strong >成品舞蹈教学：</strong>成品舞蹈为主，风格结合了欧美、日韩最潮的流行元素。</span></p><p style=\"text-indent: 2em;\"><span style=\"font-size: 14px;\"><strong >个人编舞及S0L0：</strong>个人编舞及S0L0：根据音乐来讲解编舞技巧。</span></p>');
