insert into `destoon_sell_data_5` (`itemid`, `content`) values('910','<p><font face=\"Arial\">专业肚皮舞、（教练）培训班 </font></p><p><font face=\"Arial\">赤那恩舞蹈培训学校<br />&nbsp;&nbsp;&nbsp; 肚皮舞（肚皮舞阿拉伯原名为 &quot;raks sharki&quot; 意指东方之舞），是一种带有阿拉伯风情的舞蹈形式，起源于中东地区，并在中东和巴基斯坦、印度、伊朗等其他受阿拉伯文化影响的地区取得长足发展，19世纪末传入欧美地区，至今已遍布世界各地，成为一种较为知名的国际性舞蹈。<br />&nbsp;&nbsp;&nbsp; 肚皮舞是非常女性的舞蹈，随着变化万千的快速节奏，摆动腹部、使劲的舞动臂部、胸部，这些动作，成为肚皮舞，牢不可破伝统舞技，时而优雅、时而感性、妩媚娇柔，时而傲酷，神秘，令人目不暇接。 <br />　 肚皮舞它是一种全身的运动，可以让你的腿部、腹部、肩膀以及颈部都得到充分的活动，从而提高身体的弹性和柔韧性。手臂的动作非常重要，它能表达出舞者的优雅和精巧。它不仅仅是一种运动，它也为心灵与身体建立了一种精神纽带。你可以象蝴蝶、海浪、流水一样欢快与自由。<br />&nbsp;<br />招生条件：热爱肚皮舞的人，有无基础均可，从零基础学起。 <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 愿意从事肚皮舞教练工作、舞蹈工作室创作、舞蹈表演工作以及肚皮舞爱好者。 <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 没有年龄限制 <br />培训特色：练习，教学，实践，表演合一，而且同步进行。 <br />培训费用：高级教练班6800元 <br />培训内容：肚皮舞的基本动作，体态，体能训练； <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 肚皮舞组合套路、成品舞蹈训练。（难度由易到难） <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1． 纱巾舞 <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2． 鼓舞 <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3． 面纱舞 <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 4． 阿拉伯肚皮舞 <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5． 中东地区肚皮舞 <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 6． 肚皮舞成品套路 <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 7． 肚皮舞表演套路 <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 肚皮舞的乐理知识，专业术语训练 <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 肚皮舞教学技法训练 <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 肚皮舞教学实践、演练 <br />培训时间：教会为止，一个月左右，据具体情况而定。</font></p><p><font face=\"Arial\">咨询电话13426296043<br />QQ：1215992711</font></p>');
